(156, '01fe70ef04964f3b980e46bd610f1940', '', '四川交通职业技术学院三级道路指示项目（第二次）采购项目结果公告', 1, '通知公告', '', 1, '国有资产管理处', 5, '宣传统战部', 0, '\r\n\r\n四川交通职业技术学院三级道路指示项目（第二次）采购项目结果公告\r\n\r\n', '', '<p><!--StartFragment --></p>\r\n\r\n<p >四川交通职业技术学院三级道路指示项目（第二次）采购项目结果公告</p>\r\n\r\n<p ><br />\r\n各有关单位：<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &ldquo;四川交通职业技术学院三级道路指示项目（第二次）&rdquo;（川交职院采招公【2016】-016号）因有效投标单位不足三家，流标。</p>\r\n\r\n<p >二〇一六年九月十三日</p>\r\n		\r\n		', '', '', '', '', '2016-09-13 14:15:04', '0000-00-00', '2016-09-15 06:55:44', '2016-09-13 00:00:00', 64, 0, 0, 1, 0, 0, 0, 0),
(157, '5bbdb2b0ac664fb28436edf326ee438e', '', '四川交通职业技术学院第三教学楼工程方案设计比选结果公告', 1, '通知公告', '', 1, '后勤基建处', 5, '宣传统战部', 0, '四川交通职业技术学院第三教学楼工程方案设计比选\r\n\r\n结果公告\r\n\r\n', '', '<p >四川交通职业技术学院第三教学楼工程方案设计比选</p>\r\n\r\n<p >结果公告</p>\r\n\r\n<table align="center" border="1" cellpadding="0" cellspacing="0" >\r\n	<tbody>\r\n		<tr>\r\n			<td >\r\n			<p>公告标题</p>\r\n			</td>\r\n			<td colspan="3" >\r\n			<p >四川交通职业技术学院第三教学楼工程方案设计比选结果公告</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td >\r\n			<p>比选人</p>\r\n			</td>\r\n			<td >\r\n			<p>四川交通职业技术学院</p>\r\n			</td>\r\n			<td >\r\n			<p>比选方式：</p>\r\n			</td>\r\n			<td >\r\n			<p>公开比选</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td >\r\n			<p>结果公告公布日期</p>\r\n			</td>\r\n			<td >\r\n			<p>2016年9月12日</p>\r\n			</td>\r\n			<td >\r\n			<p>结果公告结束日期</p>\r\n			</td>\r\n			<td >\r\n			<p>2016年9月14日</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan="3" >\r\n			<p>中选候选人：</p>\r\n			</td>\r\n			<td colspan="2" >\r\n			<p>第一候选人:四川王周建筑设计有限公司</p>\r\n			</td>\r\n			<td >\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2" >\r\n			<p>第二候选人:四川省兴发规划建筑设计有限公司</p>\r\n			</td>\r\n			<td >\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2" >\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td >\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td >\r\n			<p>&nbsp;联系人/联系方式：</p>\r\n			</td>\r\n			<td colspan="3" >\r\n			<p>比选人联系方式：</p>\r\n\r\n			<p>地 址：成都市温江区柳台大道东段208号</p>\r\n\r\n			<p>电 话（传真）：028-82680051</p>\r\n\r\n			<p>联系人：李老师</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td >\r\n			<p>&nbsp;备&nbsp;&nbsp;&nbsp;&nbsp;注：</p>\r\n			</td>\r\n			<td colspan="3" >\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="4" >\r\n			<p>郑重声明：</p>\r\n\r\n			<p>&nbsp;&nbsp;申请人对比选结果公告如有异议，请在公告发布之日起三个工作日内，以书面形式向比选人提出质疑，比选人将尽快予以答复。</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n&nbsp;\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p >四川交通职业技术学院</p>\r\n\r\n<p >2016年9月12日</p>\r\n		\r\n		', '', '', '', '', '2016-09-13 14: